mapper.update:
UPDATE Reply
SET
rcontent = #{rcontent}
WHERE board_seq = #{board_seq}
AND story_seq = #{story_seq}
AND reply_seq = #{reply_seq}

mapper.delete:
DELETE FROM Reply
WHERE board_seq = #{board_seq}
AND story_seq = #{story_seq}
AND reply_seq = #{reply_seq}

mapper.select:
select *
from Story s, Board b
where s.story_seq = b.story_seq
and b.writer = #{writer}
order by s.story_title;

